import { createTheme } from '@mui/material';

export const theme = createTheme({
  palette: {
    primary: {
      main: '#ff1a1a', // red
      light: '#6666ff', // blue
    },
    secondary: {
      main: '#ffff1a', 
    },
    otherColor: {
      main: '#0000cc',
    },
  },
});

// for changing primary color and making our own color
